import {
  Avatar,
  Text,
  FlatList,
  Box,
  VStack,
  Pressable,
  Button,
  HStack,
  Image,
  Spacer,
} from "native-base";
import React, { useState, useEffect, useCallback } from "react";
import { ALERT_TYPE, Toast } from "alert-toast-react-native";
import { useFocusEffect } from "@react-navigation/native";
import { ListRenderItemInfo, StyleSheet } from "react-native";
import { usePromiseTracker, trackPromise } from "react-promise-tracker";
import { storageService } from "services";
import RecipeModal from "../Modals/RecipeModal";
import NumberFormat from "react-number-format";
interface productData {
  key?: number;
  productId: number;
  branchId: number;
  itemRemain: number;
  updatedAt: string;
  product: {
    productId: number;
    productName: string;
    productPrice: number;
    productImg?: number;
    quantity?: number;
    productType?: number;
    productStatus?: string;
    productDetail?: string;
    needProcess?: number;
    product_type?: {
      typeId: number;
      typeName: string;
      typeStatus: string;
    };
    image?: {
      imgId: number;
      imgObj: string;
    };
  };
}

const ProductList = ({
  cartData,
  setCartData,
  tabIndex,
}: {
  cartData: any;
  setCartData: (value: any) => void;
  tabIndex: number;
}) => {
  const { promiseInProgress: loadingProduct } = usePromiseTracker({
    area: "loadingProduct",
  });
  const [recipeOpen, setRecipeOpen] = useState(false);
  const [recipeId, setRecipeId] = useState<any>();
  const [productArray, setProductArray] = useState<productData[]>([]);
  const [filterData, setfilterData] = useState<productData[]>([]);
  const [fetched, setFetched] = useState(false);
  const ErrorImg =
    "data:image/png;base64,";

  const isInCart = (itemId: number) => {
    if (
      cartData.filter((e: { productId: number }) => e.productId === itemId)
        .length > 0
    ) {
      return true;
    } else {
      return false;
    }
  };
  const addToCart = (item: any) => {
    setCartData((prev: any) => [...prev, item]);
  };
  const handleAdd = (item: any) => {
    if (item.product.needProcess)
      return addToCart({
        key:
          item.product.productId +
          Math.floor(Math.random() * (100000 - 1) + 1) * 100,
        productId: item.product.productId,
        productName: item.product.productName,
        productPrice: item.product.productPrice,
        quantity: 1,
        needProcess: item.product.needProcess,
      });
    if (item.itemRemain == 0 && !item.product.needProcess) {
      return Toast.show({
        type: ALERT_TYPE.DANGER,
        textBody: "สินค้าในคลังไม่เพียงพอ",
      });
    }
    if (item.itemRemain > 0 && !isInCart(item.product.productId)) {
      return addToCart({
        key:
          item.product.productId +
          Math.floor(Math.random() * (100000 - 1) + 1) * 100,
        productId: item.product.productId,
        productName: item.product.productName,
        productPrice: item.product.productPrice,
        quantity: 1,
      });
    }
  };
  const fetchProductData = (isSubscribed: boolean) => {
    void trackPromise(
      new Promise((resolve) => {
        setTimeout(() => {
          resolve(
            storageService
              .getAllProductInStorage()
              .then((res) => {
                if (isSubscribed) {
                  const recData = res.data;
                  setFetched(true);
                  setProductArray(recData);
                }
              })
              .catch((error) => {
                const resMessage =
                  (error.response &&
                    error.response.data &&
                    error.response.data.message) ||
                  error.message ||
                  error.toString();
                Toast.show({
                  type: ALERT_TYPE.DANGER,
                  textBody: resMessage,
                });
              })
          );
        }, 500);
      }),
      "loadingProduct"
    );
  };
  useFocusEffect(
    useCallback(() => {
      let isSubscribed = true;
      if (!fetched) fetchProductData(isSubscribed);

      return () => {
        isSubscribed = false;
      };
    }, [fetched])
  );
  useEffect(() => {
    const getFilter = (value: number) => {
      if (value && value != -1) {
        const filterTable = productArray.filter(
          (item) => item.product?.productType == value
        );
        setfilterData(filterTable);
      } else {
        setfilterData(productArray);
      }
    };
    getFilter(tabIndex);
    return () => {};
  }, [productArray, tabIndex]);
  const formatData = (data: any[]) => {
    const numberOfFullRows = Math.floor(data.length / 4);

    let numberOfElementsLastRow = data.length - numberOfFullRows * 4;
    while (numberOfElementsLastRow !== 4 && numberOfElementsLastRow !== 0) {
      data.push({ key: `blank-${numberOfElementsLastRow}`, empty: true });
      numberOfElementsLastRow++;
    }

    return data;
  };
  const renderItem = ({ item }: { item: any }) => {
    if (item.empty === true) {
      return <Box style={[styles.item, styles.itemInvisible]} />;
    }
    return (
      <Box style={styles.item} borderColor="light.300">
        <Image
          alt={item.product.productName || ""}
          width={"100%"}
          h="100%"
          position={"absolute"}
          source={{
            uri: item?.product?.image?.imgObj || ErrorImg,
          }}
        />

        <NumberFormat
          value={item.product.productPrice}
          displayType={"text"}
          thousandSeparator={true}
          decimalScale={0}
          fixedDecimalScale
          renderText={(formattedValue) => (
            <Text
              bg="red.700"
              fontWeight={400}
              numberOfLines={1}
              paddingLeft={2}
              paddingRight={2}
              paddingTop={1}
              paddingBottom={1}
              marginBottom={16}
              color="light.100"
              alignSelf="flex-end"
            >
              {formattedValue}฿
            </Text>
          )}
        />

        <VStack
          pl={{ md: 18, xl: 8 }}
          pr={{ md: 2, xl: 4 }}
          w="100%"
          position={"absolute"}
          bottom={0}
          bg="orange.100"
          justifyContent="center"
        >
          <Text fontWeight={600} flexWrap="wrap">
            {item.product.productName}
          </Text>

          <HStack w="100%">
            {!item.product.needProcess ? (
              <Text
                fontWeight={300}
                flexWrap="wrap"
                flex="1"
                numberOfLines={1}
                fontSize={12}
                color={item.itemRemain < 1 ? "red.500" : "light.600"}
              >
                คงเหลือ {item.itemRemain}
              </Text>
            ) : (
              <Pressable
                _pressed={{ bg: "amber.400", borderRadius: 2 }}
                onPress={() => {
                  setRecipeId(item.productId);
                  setRecipeOpen(true);
                }}
              >
                <Text
                  fontWeight={300}
                  fontSize={12}
                  flexWrap="wrap"
                  textDecorationLine="underline"
                >
                  การผสม
                </Text>
              </Pressable>
            )}
            <Spacer />
            <Pressable
              _pressed={{ bg: "amber.400", borderRadius: 2 }}
              display={isInCart(item.productId) ? "none" : "flex"}
              onPress={() => {
                handleAdd(item);
              }}
            >
              <Text
                fontWeight={300}
                fontSize={12}
                flexWrap="wrap"
                textDecorationLine="underline"
              >
                เลือก
              </Text>
            </Pressable>
          </HStack>
        </VStack>
      </Box>
    );
  };
  return (
    <>
      {recipeOpen && (
        <RecipeModal
          recipeOpen={recipeOpen}
          setRecipeOpen={setRecipeOpen}
          recipeId={recipeId}
        />
      )}
      <Box alignSelf="center" w="100%" h="100%">
        <FlatList
          numColumns={4}
          w="100%"
          h="100%"
          onRefresh={() => {
            fetchProductData(true);
          }}
          contentContainerStyle={{ paddingTop: 4 }}
          refreshing={loadingProduct}
          data={formatData(filterData == null ? productArray : filterData)}
          keyExtractor={(item: any) => item.productId}
          renderItem={renderItem}
        />
      </Box>
    </>
  );
};

export default ProductList;
const styles = StyleSheet.create({
  item: {
    alignItems: "center",
    flexDirection: "row-reverse",
    flex: 1,
    aspectRatio: 1,
    width: "100%",
    height: "100%",
    borderWidth: 1,
    margin: 0.5,
  },
  itemInvisible: {
    backgroundColor: "transparent",
    borderWidth: 0,
  },
});
