import {
  Image,
  Text,
  FlatList,
  Box,
  VStack,
  Button,
  Center,
  View,
  HStack,
} from "native-base";
import React, { useState, useCallback } from "react";
import { ALERT_TYPE, Toast } from "alert-toast-react-native";
import { useFocusEffect } from "@react-navigation/native";
import { Dimensions, StyleSheet } from "react-native";
import { usePromiseTracker, trackPromise } from "react-promise-tracker";
import { promotionService } from "services";
import dayjs from "dayjs";
import "dayjs/locale/th"; // ES 2015
import NumberFormat from "react-number-format";

const numColumns = 2;
const PromotionList = ({
  cartData,
  setCartData,
  promoCart,
  setPromoCart,
}: {
  cartData: any;
  setCartData: (value: any) => void;
  promoCart: any;
  setPromoCart: (value: any) => void;
}) => {
  const { promiseInProgress } = usePromiseTracker();

  const [promoData, setPromoData] = useState<any[]>([]);
  const ErrorImg =
    "data:image/png;base64,";

  // const isInCart = (itemId: number) => {
  //   if (
  //     cartData.filter((e: { prId: number }) => e.prId === itemId).length > 0
  //   ) {
  //     return true;
  //   } else {
  //     return false;
  //   }
  // };

  const isInCart = (promoId: number) => {
    return promoCart.findIndex(
      (e: { promoId: number }) => e.promoId == promoId
    );
  };
  const addToCart = async (promo: any) => {
    let items: any = [];
    await promotionService
      .getPromoItemToCart(promo.promoId)
      .then((res) => (items = res.data.promotion_items))
      .catch((err) => console.log(err));

    const keyArray: any[] = [];
    cartData.forEach(
      (e: { promoId: number; key: number; prId: number }, index: any) => {
        if (e.promoId === promo.promoId) {
          keyArray.push({ key: e.key, index: index, prId: e.prId });
        }
      }
    );
    if (keyArray.length > 0) {
      const tempCart = cartData;
      keyArray.forEach((element) => {
        items.forEach((entry: { count: any; productId: number }) => {
          if (element.prId == entry.productId) {
            console.log(tempCart[element.index]);
            const temp = tempCart[element.index].prCount + entry.count;
            tempCart[element.index].prCount = temp;
          }
        });
      });
      setCartData(tempCart);
    } else
      items.forEach(
        (entry: {
          productId: any;
          product: { prName: string; prPrice: any; needProcess?: any };
          count: any;
        }) => {
          {
            const forCart = {
              key:
                promo.promoId +
                Math.floor(Math.random() * (100000 - 1) + 1) * 100,
              prId: entry.productId,
              prName: "*" + entry.product.prName,
              prPrice: entry.product.prPrice,
              prCount: entry.count,
              needProcess: entry.product.needProcess,
              promoId: promo.promoId,
            };
            setCartData((prev: any) => [...prev, forCart]);
          }
        }
      );

    const index = isInCart(promo.promoId);
    if (index > -1) {
      let temp = promoCart[index].promoCount;
      temp = temp + 1;
      setPromoCart(
        Object.values({
          ...promoCart,
          [index]: { ...promoCart[index], promoCount: temp },
        })
      );
    } else {
      const data = {
        key: promo.promoId + Math.floor(Math.random() * (100000 - 1) + 1) * 100,
        promoId: promo.promoId,
        promoName: promo.promoName,
        promoPrice: promo.promoPrice,
        promoCost: promo.promoCost,
        promoCount: 1,
      };
      setPromoCart((prev: any) => [...prev, data]);
    }
  };
  const fetchPromo = (isSubscribed: boolean) => {
    void trackPromise(
      promotionService
        .getCurrentPromotion()
        .then((res) => {
          if (isSubscribed) {
            if (res) {
              const recData = res.data;
              setPromoData(recData);
            }
          }
        })
        .catch((error) => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();
          Toast.show({
            type: ALERT_TYPE.DANGER,
            textBody: resMessage,
          });
        })
    );
  };
  useFocusEffect(
    useCallback(() => {
      let isSubscribed = true;
      fetchPromo(isSubscribed);

      return () => {
        setPromoData([]);
        isSubscribed = false;
      };
    }, [])
  );

  const formatData = (data: any[], numColumns: number) => {
    const numberOfFullRows = Math.floor(data.length / numColumns);

    let numberOfElementsLastRow = data.length - numberOfFullRows * numColumns;
    while (
      numberOfElementsLastRow !== numColumns &&
      numberOfElementsLastRow !== 0
    ) {
      data.push({ key: `blank-${numberOfElementsLastRow}`, empty: true });
      numberOfElementsLastRow++;
    }

    return data;
  };

  const renderItem = ({ item }: { item: any }) => {
    if (item.empty === true) {
      return <View style={[styles.item, styles.itemInvisible]} />;
    }
    return (
      <HStack style={styles.item}>
        <Button
          colorScheme="greenalt"
          position="absolute"
          shadow={4}
          zIndex={4}
          right={{ md: "1", xl: 25 }}
          top={{ md: 1, xl: 25 }}
          alignSelf="center"
          size={{ md: 10 }}
          onPress={async () => {
            await addToCart(item);
          }}
        >
          <Text fontSize={{ md: 24 }} color="white">
            +
          </Text>
        </Button>
        <Box style={styles.imageContainer}>
          <Image
            style={styles.itemImage}
            alt={item.promoName}
            src={item.image?.imgObj != null ? item.image.imgObj : ErrorImg}
          />
        </Box>
        <Box style={styles.textBox}>
          <Text
            fontWeight={700}
            fontSize={{ md: 14, xl: 18 }}
            ellipsizeMode="tail"
            numberOfLines={1}
          >
            {item.promoName}
          </Text>
          <Text
            ellipsizeMode="tail"
            numberOfLines={2}
            fontSize={{ md: 12, xl: 16 }}
          >
            {item.promoDetail}
          </Text>
          <Text color="light.500" fontSize={{ md: 12, xl: 16 }}>
            เริ่มต้น:{" "}
            {dayjs(item.promoStart).locale("th").format("D MMMM YYYY ")}
          </Text>
          <Text color="light.500" fontSize={{ md: 12, xl: 16 }}>
            สิ้นสุด: {dayjs(item.promoEnd).locale("th").format("D MMMM YYYY ")}
          </Text>
          <Box style={styles.footerText}>
            <Text fontSize={{ md: 14, xl: 18 }}>ราคา: </Text>
            <NumberFormat
              value={item.promoCost}
              displayType={"text"}
              thousandSeparator={true}
              decimalScale={2}
              fixedDecimalScale
              renderText={(formattedValue) => (
                <Text
                  textDecorationLine="line-through"
                  fontSize={{ md: 14, xl: 18 }}
                >
                  {" "}
                  {formattedValue}{" "}
                </Text>
              )}
            />
            <NumberFormat
              value={item.promoPrice}
              displayType={"text"}
              thousandSeparator={true}
              decimalScale={2}
              fixedDecimalScale
              renderText={(formattedValue) => (
                <Text color="red.600" fontSize={{ md: 14, xl: 18 }}>
                  {" "}
                  {formattedValue} บาท
                </Text>
              )}
            />
          </Box>
        </Box>
      </HStack>
    );
  };
  return (
    <>
      <Box alignSelf="center" w="100%">
        <FlatList
          numColumns={numColumns}
          onRefresh={() => {
            fetchPromo(true);
          }}
          refreshing={promiseInProgress}
          data={formatData(promoData, numColumns)}
          keyExtractor={(item: any) => item.promoId}
          renderItem={renderItem}
        />
      </Box>
    </>
  );
};
export default PromotionList;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginVertical: 20,
  },
  item: {
    borderWidth: 1,
    borderRadius: 8,
    flex: 1,
    margin: 16,
    height: Dimensions.get("window").width / 6, // approximate a square
    padding: Dimensions.get("window").width / 84,
  },
  itemInvisible: {
    backgroundColor: "transparent",
    borderWidth: 0,
  },
  itemText: {
    color: "#000",
  },
  imageContainer: { flex: 3 },
  itemImage: {
    width: "100%",
    height: "100%",
    resizeMode: "contain",
  },
  textBox: {
    flex: 4,
    paddingVertical: 24,
    paddingHorizontal: 12,
  },
  footerText: {
    width: "100%",
    position: "absolute", //Here is the trick
    flexDirection: "row",
    justifyContent: "flex-end",
    bottom: 12, //Here is the trick
    right: 0,
  },
});
