import React, { useState, useEffect } from "react";
import {
  StatusBar,
  Box,
  Center,
  HStack,
  VStack,
  Text,
  Badge,
  Avatar,
} from "native-base";
import { Navigation } from "../hooks/navigation";
import { CartSidebar } from "../components";
import { Ionicons } from "@expo/vector-icons";
import MaterialIcons from "react-native-vector-icons/MaterialIcons";
import ProductList from "../components/ProductList";
import MainMenuTab from "../components/MainMenuTab";
import branchService from "../services/branch.service";

interface Props {
  navigation: Navigation;
  children?: JSX.Element;
}
type IBranchObj = {
  brId: number;
  brName: string;
  brAddr: string;
  brTel: string;
  brStatus: string;
  brImg: string;
  image: {
    imgId: number;
    imgObj: string;
  };
};
type ICartArray = {
  key: number;
  prId: string;
  prName: string;
  prPrice: string;
  prCount: string;
};
const MainMenuScreen: React.FC<Props> = ({ navigation, children }) => {
  const [tabIndex, setTabIndex] = useState<number>(-1);
  const [branchData, setBranchData] = useState<IBranchObj>({} as IBranchObj);
  const ErrorImg =
    "data:image/png;base64,";

  useEffect(() => {
    navigation.addListener("beforeRemove", (e: any) => {
      e.preventDefault();
      return;
    });
  }, [navigation]);

  useEffect(() => {
    let isSubscribed = true;
    branchService
      .getCurrentBranch()
      .then((res) => {
        isSubscribed ? setBranchData(res.data) : null;
      })
      .catch((err) => console.log(err));
    return () => {
      isSubscribed = false;
    };
  }, []);
  const [cartData, setCartData] = useState<ICartArray[]>([]);
  return (
    <>
      <StatusBar
        translucent
        backgroundColor="transparent"
        barStyle="light-content"
      />
      <Box safeAreaTop bg="coolGray.500" />
      <Center flex="1" bg="#FFF">
        <HStack w="100%" flex="1">
          <VStack w="100%" flex={{ md: "3", xl: "4" }}>
            {children}
            <VStack
              borderWidth={1}
              w="95%"
              borderRadius={5}
              flex="10"
              alignSelf="center"
              alignItems="center"
              mt="4"
              mb={{ md: "10%", xl: "6%" }}
              justifyContent="center"
            >
              <HStack flex="1" alignItems="center">
                <MainMenuTab tabIndex={tabIndex} setTabIndex={setTabIndex} />
              </HStack>
              <VStack w="100%" flex="10">
                <ProductList
                  cartData={cartData}
                  setCartData={setCartData}
                  tabIndex={tabIndex}
                />
              </VStack>
            </VStack>
          </VStack>

          {/*Sidebar Component */}
          <CartSidebar cartData={cartData} setCartData={setCartData} />
          {/*Sidebar Component */}
        </HStack>
      </Center>
    </>
  );
};

export default MainMenuScreen;
