import React, { useState, useEffect } from "react";
import {
  StatusBar,
  Box,
  Center,
  HStack,
  VStack,
  Text,
  Badge,
  Avatar,
  Alert,
} from "native-base";
import { Navigation } from "../hooks/navigation";
import { CartSidebar, ReceiptSidebar } from "../components";
import { Ionicons } from "@expo/vector-icons";
import MaterialIcons from "react-native-vector-icons/MaterialIcons";
import ProductList from "../components/ProductList";
import MainMenuTab from "../components/MainMenuTab";
import branchService from "../services/branch.service";

interface Props {
  navigation: Navigation;
}
type IBranchObj = {
  brId: number;
  brName: string;
  brAddr: string;
  brTel: string;
  brStatus: string;
  brImg: string;
  image: {
    imgId: number;
    imgObj: string;
  };
};
const MainMenuScreen: React.FC<Props> = ({ navigation }) => {
  const [branchData, setBranchData] = useState<IBranchObj>({} as IBranchObj);
  const ErrorImg =
    "data:image/png;base64,";
  useEffect(() => {
    const unsubscribe = navigation.addListener(
      "tabPress",
      (e: { preventDefault: () => void }) => {
        e.preventDefault();
        navigation.navigate("MainScreen");
      }
    );

    return unsubscribe;
  }, [navigation]);
  useEffect(() => {
    let isSubscribed = true;
    branchService
      .getCurrentBranch()
      .then((res) => {
        if (isSubscribed) {
          setBranchData(res.data);
        }
      })
      .catch((err) => console.log(err));
    return () => {
      isSubscribed = false;
    };
  }, []);
  return (
    <>
      <StatusBar
        translucent
        backgroundColor="transparent"
        barStyle="light-content"
      />
      <Box safeAreaTop bg="coolGray.500" />
      <Center flex="1" bg="#FFF">
        <HStack w="100%" flex="1">
          <VStack w="100%" flex={{ md: "3", xl: "4" }}>
            <HStack
              w="100%"
              flex="1"
              bg="browntheme.500"
              justifyContent="center"
              alignItems="center"
            >
              <HStack w="95%" justifyContent="center" alignItems="center">
                <Text
                  color="white"
                  flex="1"
                  fontSize={24}
                  fontFamily="body"
                  fontWeight={600}
                >
                  Codesom
                </Text>
                <Text
                  color="white"
                  fontSize={24}
                  fontFamily="body"
                  fontWeight={600}
                  mx="4"
                >
                  {branchData.brName}
                </Text>
                <Avatar
                  size={{ md: 12, xl: 16 }}
                  mx="4"
                  source={{
                    uri:
                      branchData.image && branchData.image.imgObj
                        ? branchData.image.imgObj
                        : ErrorImg,
                  }}
                />
                <Ionicons
                  name="receipt"
                  size={36}
                  color="white"
                  style={{ marginRight: 16 }}
                />
                <MaterialIcons
                  name="notifications"
                  color="white"
                  size={48}
                  style={{ transform: [{ rotate: "10deg" }] }}
                />
                <Badge // bg="red.400"
                  colorScheme="danger"
                  rounded="full"
                  mb={4}
                  ml="-12"
                  mr={8}
                  zIndex={1}
                  variant="solid"
                  _text={{
                    fontSize: 12,
                  }}
                  borderWidth="2"
                  borderColor="white"
                >
                  2
                </Badge>
              </HStack>
            </HStack>
            <VStack
              borderWidth={1}
              w="95%"
              borderRadius={5}
              flex="10"
              alignSelf="center"
              alignItems="center"
              mt="4"
              mb={{ md: "10%", xl: "6%" }}
              justifyContent="center"
            >
              <HStack flex="1" alignItems="center"></HStack>
              <VStack w="100%" flex="10"></VStack>
            </VStack>
          </VStack>

          {/*Sidebar Component */}
          <ReceiptSidebar />
          {/*Sidebar Component */}
        </HStack>
      </Center>
    </>
  );
};

export default MainMenuScreen;
